import { Utils } from '../utils.js';
import { _ } from '../localizer.js';

const CAPTURE_WIDTH = 1024;
const CAPTURE_HEIGHT = 768;
const CAPTURE_TIMEOUT = 60000 * 2; // 2 minutes
const CHECK_COMPLETE_INTERVAL = 1000;
const CHECK_COMPLETE_INTERVAL_FINAL = 3000;
const DISPLAY_HIDDEN_WINDOW_MONITOR = 5;
const START_TIME = Date.now();

const CANNOT_CAPTURE_REGEXPS = [/^chrome:\/\//i, /^chrome-extension:\/\//i, /^moz-extension:\/\//i];
const FAILED_IMAGE = {
	src: 'data:image/png;base64,',
	size: {
		width: 30,
		height: 30,
	},
};

function RemoveWindowListener(captureWindowId) {
	const self = this;

	function _removeWindowListener(removedWindowId) {
		if (removedWindowId === captureWindowId) {
			return self.stop();
		}

		chrome.windows.getAll(function (windows) {
			if (windows.length === 1 && windows[0].id === captureWindowId) {
				chrome.windows.remove(captureWindowId);
			}
		});
	}
	chrome.windows.onRemoved.addListener(_removeWindowListener);
	this.stop = function () {
		chrome.windows.onRemoved.removeListener(_removeWindowListener);
	};
}

const HiddenCaptureModule = function (fvdSpeedDial) {
	const isLinux = navigator.platform.toLowerCase().indexOf('linux') !== -1;
	const isMac = navigator.platform.toLowerCase().indexOf('mac') === 0;
	const isWin = navigator.platform.toLowerCase().indexOf('win') === 0;

	this.capture = function (params, callback) {
		function returnFailedImage() {
			setTimeout(function () {
				callback({
					dataUrl: FAILED_IMAGE.src,
					title: _('error_getting_title'),
					thumbSize: {
						width: FAILED_IMAGE.size.width,
						height: FAILED_IMAGE.size.height,
					},
				});
			}, 0);
		}

		for (let i = 0; i !== CANNOT_CAPTURE_REGEXPS.length; i++) {
			if (CANNOT_CAPTURE_REGEXPS[i].test(params.url)) {
				return returnFailedImage();
			}
		}

		if (!params.saveImage) {
			// special case, capture only title, do not create separate tab due to #1298
			// only make xmlhttprequest to parse a title
			Utils.getTitleForUrl(params.url, function (title) {
				callback({
					title: title || '',
				});
			});
			return;
		}

		if (typeof params === 'string') {
			params = {
				url: params,
			};
		}

		params.width = params.width || fvdSpeedDial.SpeedDial.getMaxCellWidth() * 2;

		const createUrl = params.url;

		/*
      var winCreateParams = {
        url: createUrl,
        focused: false,
        left: 100000,
        top: 100000,
        width: isWin ? 100 : 1,
        height: isWin ? 100 : 1,
        type: 'popup'
      };
      */
		const winCreateParams = {
			url: createUrl,
			state: 'minimized',
			// type: 'popup',
			// focused: false,
		};

		function onWindowCreate(w) {
			if (!w.tabs || !w.tabs.length) {
				// close window
				chrome.windows.remove(w.id);
				return;
			}

			let ctimeout = null;
			const timeout = setTimeout(function () {
				try {
					clearTimeout(ctimeout);
				} catch (ex) {
					console.warn(ex);
				}
				chrome.windows.remove(w.id);
				callback(null);
			}, CAPTURE_TIMEOUT);

			Utils.Async.chain([
				function (next) {
					// can't hide window on Mac OS, by setting large positions
					if (isMac) {
						return next();
					}

					let monitor = 0;

					Utils.Async.cc(function (ccCallback) {
						const minusw = localStorage.getItem('minusw') || 1;
						const minush = localStorage.getItem('minush') || 1;

						chrome.windows.update(
							w.id,
							{
								top: (screen.availHeight || screen.height) - minush,
								left: (screen.availWidth || screen.width) - minusw,
							},
							function () {
								monitor++;

								if (monitor === DISPLAY_HIDDEN_WINDOW_MONITOR) {
									// restore size
									chrome.windows.update(
										w.id,
										{
											width: CAPTURE_WIDTH,
											height: CAPTURE_HEIGHT,
										},
										function () {
											next();
										}
									);
									return;
								}

								ccCallback();
							}
						);
					});
				},
				function () {
					if (isLinux || isMac) {
						chrome.windows.update(w.id, {
							state: 'minimized',
						});
					}
				},
			]);

			const tab = w.tabs[0];

			if (String(tab.url).indexOf('chrome-error://') !== -1) {
				return returnFailedImage();
			}

			// mute tab
			chrome.tabs.update(tab.id, {
				muted: true,
			});

			chrome.scripting.executeScript(
				{
					target: { tabId: tab.id },
					files: ['/content-scripts/hiddencapture.js'],
				},
				() => {}
			);

			let isFinalTimeout = false;

			function checkTimeout(interval) {
				ctimeout = setTimeout(function () {
					chrome.tabs.get(tab.id, function (tabInfo) {
						if (!tabInfo) {
							// tab closed
							clearTimeout(timeout);
							return callback(null);
						}

						if (String(tabInfo.url).indexOf('chrome-error://') !== -1) {
							return returnFailedImage();
						}

						if (!params.saveImage && tabInfo.title) {
							// capture only title
							chrome.windows.remove(w.id);
							return callback({
								title: tabInfo.title,
							});
						}

						if (tabInfo.status === 'complete') {
							if (isFinalTimeout) {
								capture(tabInfo);
							} else {
								isFinalTimeout = true;
								checkTimeout(CHECK_COMPLETE_INTERVAL_FINAL);
							}
						} else {
							isFinalTimeout = false;
							checkTimeout();
						}
					});
				}, interval || CHECK_COMPLETE_INTERVAL);
			}

			function normailzeWithCheck(callback, attemptNum) {
				if (attemptNum > 30) {
					return callback();
				}

				attemptNum = attemptNum || 0;
				const updateData = {
					state: 'normal',
					focused: false,
					width: CAPTURE_WIDTH,
					height: CAPTURE_HEIGHT,
				};

				chrome.windows.update(w.id, updateData, function (win) {
					chrome.windows.get(w.id, function (wInfo) {
						if (wInfo.state !== 'normal') {
							normailzeWithCheck(callback, attemptNum + 1);
						} else {
							callback();
						}
					});
				});
			}

			function capture(tab) {
				Utils.Async.chain([
					function (chainCallback) {
						// replace alerts before capture, because onbeforeunload alert can block page closing
						chrome.scripting.executeScript(
							{
								target: { tabId: tab.id },
								func: () => {
									window.postMessage({ action: 'fvdsd:hiddenCapture:__replaceAlerts' }, '*');
								},
							},
							() => {
								chainCallback();
							}
						);
					},

					function (chainCallback) {
						chrome.windows.get(w.id, function (wInfo) {
							if (wInfo.state !== 'normal') {
								normailzeWithCheck(chainCallback);
							} else {
								chainCallback();
							}
						});
					},

					function () {
						chrome.windows.update(
							w.id,
							{
								width: CAPTURE_WIDTH,
								height: CAPTURE_HEIGHT,
							},
							function () {
								setTimeout(function () {
									chrome.tabs.captureVisibleTab(w.id, function (dataUrl) {
										clearTimeout(timeout);
										chrome.windows.remove(w.id);

										if (!dataUrl) {
											console.error('Fail to capture tab ', params.url, chrome.runtime.lastError);
											return returnFailedImage();
										}

										fvdSpeedDial.ThumbMaker.getImageDataPath(
											{
												imgUrl: dataUrl,
												screenWidth: params.width,
											},
											function (thumbUrl, thumbSize) {
												callback({
													dataUrl: thumbUrl,
													title: tab.title,
													thumbSize: thumbSize,
												});
											}
										);
									});
								}, 500);
							}
						);
					},
				]);
			}
			checkTimeout();
		}
		let delay = 0;
		const wait = 15000;
		const shift = Date.now() - START_TIME;

		if (shift < wait) {
			delay = wait - delay;
		}

		setTimeout(() => {
			try {
				chrome.windows.create(winCreateParams, onWindowCreate);
			} catch (err) {
				console.log('something went wrong during hidden capture:', err, 'skip');
				returnFailedImage();
			}
		}, delay);
	};
};

export default HiddenCaptureModule;
